import React, { useState } from "react";
import ResendOTP  from "./ResendOTP";
import OTPInput from './Hooks/OTPRender'
import './'

const OtpInputCard = ({ title, resendOTP, ...rest }) => {

    const [OTP, setOTP] = useState("");
    const handleSubmit = ()=>{
        if (OTP.length <= 5) {
            alert("Fill the otp")
        }
    }
  return (
    <div
      style={{
        padding: 12,
      }}
    >
      <div className="title" style={{ marginBottom: 19 }}>{title}</div>
      <OTPInput value={OTP} onChange={setOTP} {...rest} />
      <input type="submit" onClick={handleSubmit}></input>
    </div>
  );
};

function Otp() {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        flexDirection: "column",
      }}>
      <h2>OTP</h2>
      <div>
        <OtpInputCard
          autoFocus
          OTPLength={6}
          otpType="number"
          disabled={false}
          resendOTP={{}}
          />
        <ResendOTP handelResendClick={() => alert("Resend clicked")} />
      </div>
    </div>
  );
}

export default Otp;
