import React, { useState } from "react";
import ResendOTP  from "./ResendOTP";
import OTPInput from './Hooks/OTPRender'

const OtpInputCard = ({ title, resendOTP, ...rest }) => {
  const [OTP, setOTP] = useState("");
  return (
    <div
      style={{
        padding: 12,
      }}
    >
      <div style={{ marginBottom: 12 }}>{title}</div>
      <OTPInput value={OTP} onChange={setOTP} {...rest} />
    </div>
  );
};

function Otp() {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      <h2>OTP Input React</h2>
      <div>
        <OtpInputCard
          title="Secrate input with auto focus on mount"
          autoFocus
          OTPLength={4}
          placeholder={[0, 0, 0, 0]}
          otpType="number"
          disabled={false}
          resendOTP={{}}
          secure
        />
        <ResendOTP handelResendClick={() => alert("Resend clicked")} />
        <ResendOTP
          renderTime={() => React.Fragment}
          renderButton={(buttonProps) => {
            return (
              <button {...buttonProps}>
                {buttonProps.remainingTime !== 0 ? `Please wait for ${buttonProps.remainingTime} sec` : "Resend"}
              </button>
            );
          }}
        />




        <OtpInputCard
          title="6 length otp Inputs"
          // autoFocus
          OTPLength={6}
          otpType="any"
          disabled={false}
          // secure
        />

      </div>
    </div>
  );
}

export default Otp;
