import React, { useState } from "react";
import { ToastContainer, toast } from 'react-toastify';
import ResendOTP from "./ResendOTP";
import OTPInput from './Hooks/OTPRender'
import './Otp.css'

const OtpInputCard = ({ title, resendOTP, ...rest }) => {

    const [OTP, setOTP] = useState("");
    const handleSubmit = () => {
        if (OTP.length <= 5) {
            toast.error('Please fill the OTP', {
                position: "top-right",
                autoClose: 1200,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                });
        }
    }
    return (
        <div
            style={{
                padding: 19,

            }}
        >
            <div className="title" style={{ marginBottom: 19 }}>{title}</div>
            <OTPInput value={OTP} onChange={setOTP} {...rest} />
            <div class="box-1">
                <div class="btn btn-one" onClick={handleSubmit}>
                    <span>Continue</span>
                </div>
            </div>

        </div>
    );
};

function Otp() {
    return (
        <div
            style={{
                display: "flex",
                alignItems: "center",
                flexDirection: "column",
            }}>
            <h2>OTP</h2>
            <div className="container">
                <OtpInputCard
                    autoFocus
                    OTPLength={6}
                    otpType="number"
                    disabled={false}
                    resendOTP={{}}
                />
                <ResendOTP handelResendClick={() => alert("Resend clicked")} />
            </div>
        </div>
    );
}

export default Otp;
