import React, { useState } from "react";
import ResendOTP from "./ResendOTP";
import OTPInput from './Hooks/OTPRender'
import Swal from "sweetalert2";
import './Otp.css'

const OtpInputCard = ({ title, resendOTP, ...rest },props) => {

    const [OTPS, setOTP] = useState("");
    const getToken = localStorage.getItem('token')
    const config = {
        otp: OTPS,
        token : getToken
    }
    const handleSubmit = async () => {
        try 
        {
            if (OTPS.length <= 5) {
                Swal.fire({
                    icon: 'error',
                    text: 'Please Fill all the Filed of Otp ',
                })
            }
            if (OTPS.length === 6) {
                const response = await fetch('http://localhost:8000/otp',
                    {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                            "Authorization": Bearer
                        },
                        body: JSON.stringify(config)
                    });
                const Response = await response.json()
                if (Response.success === true) {
                    props.history.push('/home')
                }
                if (Response.success === false){

                }
                if (response.status === 211) {
                    console.log(response);
                }

            }
        }
        catch (error) { console.log(error) };
    }
    return (
        <div
            style={{
                padding: 19,

            }}
        >
            <div style={{ marginBottom: 19 }}>{title}</div>
            <OTPInput value={OTPS} onChange={setOTP} {...rest} />
            <div className="box-1">
                <div className="btn btn-one" onClick={handleSubmit}>
                    <span>Continue</span>
                </div>
            </div>
            <div  >
                <ResendOTP />
            </div>
        </div>
    );
};

function Otp() {

    return (
        <div
            style={{
                display: "flex",
                alignItems: "center",
                flexDirection: "column",
            }}>
            <h2>OTP</h2>
            <div className="container">
                <OtpInputCard
                    autoFocus
                    OTPLength={6}
                    otpType="number"
                    disabled={false}
                    resendOTP={{}}
                />

            </div>
        </div>
    );
}

export default Otp;
