import React, { useState } from "react";
import ResendOTP  from "./ResendOTP";
import OTPInput from './Hooks/OTPRender'

const [OTP, setOTP] = useState("");
function handleSubmit(){
    console.log(OTP);
}

const OtpInputCard = ({ title, resendOTP, ...rest }) => {






  return (
    <div
      style={{
        padding: 12,
      }}
    >
      <div style={{ marginBottom: 12 }}>{title}</div>
      <OTPInput value={OTP} onChange={setOTP} {...rest} />
    </div>
  );
};

function Otp() {
  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        flexDirection: "column",
      }}>
      <h2>OTP Input React</h2>
      <div>
        <OtpInputCard
          autoFocus
          OTPLength={6}
          placeholder={[0, 0, 0, 0, 0, 0]}
          otpType="number"
          disabled={false}
          resendOTP={{}}
          onchan
          secure/>
        <ResendOTP handelResendClick={() => alert("Resend clicked")} />
        <input type="submit" onSubmit={handleSubmit}></input>
      </div>
    </div>
  );
}

export default Otp;
